import { useState } from "react"
import {makeOptions, handleHttpErrors} from "../facades/fetchUtils";
import {bookURL as url} from "../facades/settings"


export default function DeleteBook({books}){

    const[bookId, setBookId] = useState("");
    const[selectedBook, setSelectedBook] = useState({});

    const onChange = (evt) => {
        setBookId(evt.target.value)
    }

      const onClick = (evt) =>{
        evt.preventDefault();
        const options = makeOptions("DELETE", true);
        fetch(url + bookId, options)
            .then(handleHttpErrors)
            .then((data) => {})
        }

        const Push = (book)=>{
            //kloner book opbjektet
            setSelectedBook({...book})
            console.log(book)
        }


    return(
        <div>
            <table>
                <thead><tr><td>isbn</td>
                <td>Title</td>
                <td>author</td>
                <td>publisher</td>
                <td>publish year</td></tr></thead>
                <tbody>{books.map((p) => {
                    return (
                        <tr onClick={(p) => Push(p)} key={p.isbn}>
                          <td>{p.isbn}</td>
                          <td>{p.title}</td>
                          <td>{p.authors}</td>
                          <td>{p.publisher}</td>
                          <td>{p.publishYear}</td></tr>
                        )
                })}</tbody>
            </table>
            <form onChange={onChange}>
                <label for="bookId">ID of book to be deleted:</label><br/>
                <input type="text" id="bookId"/><br/>
                <button type="button" onClick={onClick}>Delete</button>
            </form>
            <form onChange={onChange}> 
                <label for="isbn">First name:</label><br/>
                <input value={selectedBook.isbn} type="text" id="isbn"/><br/>
                <label for="title">Last name:</label><br/>
                <input value={selectedBook.title} type="text" id="title" name="lname"/><br/>
                <label for="authors">Last author:</label><br/>
                <input value={selectedBook.authors} type="text" id="authors" name="lname"/><br/>
                <label for="publishers">publisher</label><br/>
                <input value={selectedBook.publicers}type="text" id="publishers" name="lname"/><br/>
                <label for="publishYear">publishYear:</label><br/>
                <input value={selectedBook.publishYear}type="text" id="publishYear" name="lname"/><br/>
                <button type="button" onClick={onClick}>Send</button>
            </form>
        </div>
    )
}
